package com.stjohn.introduction;
//program to demonstrate on primitive data types
public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte bytemax = 127;
		short shortmax = 32767;
		long longmax = 6785678899999L;
		char var1 = 'N';
		boolean flag = true;
		
		
		System.out.println("bytemax value is: "+bytemax);
		System.out.println("shortmax value is: "+shortmax);
		System.out.println("longmax value is: "+longmax);
		System.out.println("char value is: "+var1);
		System.out.println("boolean value is: "+flag);
	}

}
